import React, { useState, useReducer, useEffect } from 'react'

class MyCount extends React.Component {
  state = {
    count: 0
  }
  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState({ count: this.state.count + 1 })
    }, 1000)
  }
  componentWillUnmount() {
    if (this.interval) {
      clearInterval(this.interval)
    }
  }
  render () {
    return <span>{ this.state.count }</span>
  }
}

function countReducer(state, action) {
  switch (action.type) {
    case 'add':
      return state + 1
    case 'minus':
      return state - 1
    default:
      return state
  }
}

function MyCountFunc() {
  // const [ count, setCount ] = useState(0) // [a, b]

  const [ count, dispatchCount ] = useReducer(countReducer, 0)
  const [ name, setName ] = useState('jock')

  
  // useEffect(() => {
  //   const interval = setInterval(() => {
  //     // setCount用法
  //     // setCount(1)
  //     // setCount((c) => c + 1 )
  //     // setCount(c => c + 1)
  //     dispatchCount({ type: 'add' })
  //   }, 1000)
  //   return () => clearInterval(interval)
  // }, [])

  useEffect(() => {
    console.log('effect invoked') // 后执行
    return () => console.log('effect deteched') // 先执行
  }, [count])  // 只有数组中的状态发生变化时才会先卸载return的值，在重新执行上面的部分， 若为空数组，则每次发生状态的变更都会执行卸载然后执行函数

  return (
    <div>
      <input value={ name } onChange={(e) => setName(e.target.value)} />
      <button onClick={() => dispatchCount({type: 'add'})}>{ count }</button>
    </div>
  )
}

export default MyCountFunc
